void Trapezy(double a, double b, int n){
	float dx, calka=0;
	int i;
	
	dx = (b - a) / (float)n;
	
	for (i=1; i<n; i++){
		calka += func(a + i * dx);
	}
	calka += (func(a) + func(b)) / 2;
	calka *= dx;

	printf("<Trapezy>Wartosc calki dla a=%f b=%f n=%d wynosi w przyblizeniu %f\n",a, b, n, calka);
}

